defmodule Pummpcomm.History.ChangeBasalProfileTest do
  use ExUnit.Case

  test "Change Basal Profile" do
    {:ok, history_page} =
      Base.decode16(
        ""
      )

    decoded_events = Pummpcomm.History.decode_records(history_page, %{})

    assert {:change_basal_profile, %{timestamp: ~N[2008-01-01 00:00:00], raw: ^history_page}} =
             Enum.at(decoded_events, 0)
  end
end
